import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Navbar from "./components/Navbar";
import FooterBottom from "./components/FooterBottom";
import Signup from "./components/Signup";
import Login from "./components/Login";
import Dashboard from "./components/Dashboard";
import Chat from "./components/Chat"; // Import the updated Chat component
import AboutUs from './components/AboutUs';
import Services from './components/Services';
import "./App.css";
import '@fortawesome/fontawesome-free/css/all.min.css';
// Home Component
function Home() {
  return (
    <div className="home-page">
      <Navbar />
      <main>
        {/* Hero Section */}
        <section className="hero-section">
          <div className="hero-content">
            <h1>Welcome to EduBot</h1>
            <p>Your AI-Powered Educational Assistant</p>
            <div className="hero-buttons">
              <a href="/signup" className="primary-btn">
                <i className="fas fa-user-plus"></i> Get Started
              </a>
              <a href="/about" className="secondary-btn">
                <i className="fas fa-info-circle"></i> Learn More
              </a>
            </div>
          </div>
        </section>

        {/* Features Section */}
        <section className="features-section">
          <div className="section-container">
            <h2>Why Choose EduBot?</h2>
            <div className="features-grid">
              <div className="feature-card">
                <i className="fas fa-robot"></i>
                <h3>24/7 AI Support</h3>
                <p>Get instant answers to your queries anytime, anywhere.</p>
              </div>
              <div className="feature-card">
                <i className="fas fa-graduation-cap"></i>
                <h3>Admission Guidance</h3>
                <p>Comprehensive support for admission processes.</p>
              </div>
              <div className="feature-card">
                <i className="fas fa-clock"></i>
                <h3>Real-time Assistance</h3>
                <p>Quick and accurate responses to your questions.</p>
              </div>
            </div>
          </div>
        </section>

        {/* Explore Section */}
        <section className="explore-section">
          <div className="section-container">
            <div className="explore-content">
              <div className="explore-text">
                <h2>Explore EduBot</h2>
                <p>
                  EduBot is designed to assist students around the clock, providing instant
                  answers to admission-related queries. With its advanced AI capabilities,
                  it ensures a seamless experience for users.
                </p>
                <a href="/chat" className="explore-btn">
                  <i className="fas fa-comments"></i> Start Chatting
                </a>
              </div>
              <div className="robot-image-container">
                <img src="/robot.png" alt="AI Robot" className="robot-image" />
              </div>
            </div>
          </div>
        </section>
      </main>
      <FooterBottom />
    </div>
  );
}

// Protected Route for Dashboard and Chat
const ProtectedRoute = ({ element }) => {
  const loggedInUser = JSON.parse(localStorage.getItem("loggedInUser"));
  return loggedInUser ? element : <Navigate to="/login" />;
};

function App() {
  return (
    <Router>
      <div>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/signup" element={<Signup />} />
          <Route path="/login" element={<Login />} />
          <Route path="/about" element={<AboutUs />} />
          <Route path="/services" element={<Services />} />
          <Route path="/dashboard" element={<ProtectedRoute element={<Dashboard />} />} />
          <Route path="/chat" element={<ProtectedRoute element={<Chat />} />} /> {/* Chat route */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
