import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Chat = () => {
  const [question, setQuestion] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [lang, setLang] = useState("en");
  const [voiceOutputEnabled, setVoiceOutputEnabled] = useState(false);
  const [isProfileOpen, setIsProfileOpen] = useState(false);
  const [isHistoryOpen, setIsHistoryOpen] = useState(false);
  const [savedHistories, setSavedHistories] = useState([]);

  const navigate = useNavigate();
  const loggedInUser = JSON.parse(localStorage.getItem("loggedInUser")) || {};

  const languages = [
    { code: "en", name: "English" },
    { code: "te", name: "Telugu" },
    { code: "hi", name: "Hindi" },
  ];

  const handleTextChat = async (newSession = false) => {
    if (!question.trim()) return;

    setChatHistory((prev) => [
      ...prev,
      { type: "user", text: question, timestamp: Date.now() },
    ]);

    setLoading(true);
    setQuestion("");

    try {
      const textRes = await axios.post("http://localhost:8000/chat", {
        question,
        lang,
        new_session: newSession,
      });

      let audioUrl = null;
      if (voiceOutputEnabled) {
        const voiceRes = await axios.post(
          "http://localhost:8000/voice-output",
          { question, lang, new_session: newSession },
          { responseType: "blob" }
        );
        const audioBlob = new Blob([voiceRes.data], { type: "audio/mp3" });
        audioUrl = URL.createObjectURL(audioBlob);
      }

      setChatHistory((prev) => [
        ...prev,
        {
          type: "bot",
          text: textRes.data.response,
          audioUrl: audioUrl,
          timestamp: Date.now(),
        },
      ]);
    } catch (error) {
      setChatHistory((prev) => [
        ...prev,
        { type: "bot", text: "Error: Could not get a response.", timestamp: Date.now() },
      ]);
      console.error("Chat Error:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleVoiceInput = () => {
    const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();
    recognition.lang = lang;
    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setQuestion(transcript);
    };
    recognition.onerror = (event) => {
      setChatHistory((prev) => [
        ...prev,
        { type: "bot", text: "Error: Could not recognize speech.", timestamp: Date.now() },
      ]);
      console.error(event.error);
    };
    recognition.start();
  };

  const toggleVoiceOutput = () => {
    setVoiceOutputEnabled((prev) => !prev);
  };

  const saveChatHistory = () => {
    if (chatHistory.length === 0) return;

    const now = Date.now();
    const newSession = {
      id: `${loggedInUser?.email}-${now}`,
      userEmail: loggedInUser?.email,
      timestamp: now,
      history: [...chatHistory],
      question: chatHistory.find((item) => item.type === "user")?.text || "Untitled",
    };

    const existingHistories = JSON.parse(localStorage.getItem("chatHistories") || "[]");
    const sevenDaysAgo = now - 604800000;
    const filteredHistories = existingHistories.filter(
      (session) => session.timestamp > sevenDaysAgo
    );

    const updatedHistories = [...filteredHistories, newSession];
    localStorage.setItem("chatHistories", JSON.stringify(updatedHistories));
    setSavedHistories(updatedHistories);
  };

  const startNewChat = () => {
    if (chatHistory.length > 0) {
      saveChatHistory();
    }
    setChatHistory([]);
    setQuestion("");
  };

  const toggleProfileDropdown = () => {
    setIsProfileOpen((prev) => !prev);
    setIsHistoryOpen(false);
  };

  const handleLogout = () => {
    localStorage.removeItem("loggedInUser");
    navigate("/");
  };

  const toggleHistoryDropdown = () => {
    setIsHistoryOpen((prev) => !prev);
    setIsProfileOpen(false);

    if (!isHistoryOpen) {
      const existingHistories = JSON.parse(localStorage.getItem("chatHistories") || "[]");
      const sevenDaysAgo = Date.now() - 604800000;
      const userHistories = existingHistories
        .filter(
          (session) =>
            session.userEmail === loggedInUser?.email && session.timestamp > sevenDaysAgo
        )
        .sort((a, b) => b.timestamp - a.timestamp);
      setSavedHistories(userHistories);
    }
  };

  const loadChatSession = (session) => {
    setChatHistory(session.history);
    setIsHistoryOpen(false);
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter" && !loading) {
      handleTextChat(false);
    }
  };

  return (
    <div style={styles.pageContainer}>
      <header style={styles.header}>
        <div style={styles.headerRight}>
          <button
            onClick={startNewChat}
            style={styles.newChatIcon}
            disabled={loading}
            title="Start New Chat"
          >
            ➕
          </button>
          <button
            onClick={toggleHistoryDropdown}
            style={styles.historyIcon}
            disabled={loading}
            title="View Chat History"
          >
            🕒
          </button>
          {isHistoryOpen && (
            <div style={styles.historyDropdown}>
              {savedHistories.length === 0 ? (
                <div style={styles.dropdownItem}>No saved history</div>
              ) : (
                savedHistories.map((session) => (
                  <div
                    key={session.id}
                    style={styles.historyItem}
                    onClick={() => loadChatSession(session)}
                  >
                    <span>{session.question}</span>
                    <span style={styles.timestamp}>
                      {new Date(session.timestamp).toLocaleTimeString()}
                    </span>
                  </div>
                ))
              )}
            </div>
          )}
          <div style={styles.profileContainer}>
            <div
              style={styles.profileTrigger}
              onClick={toggleProfileDropdown}
            >
              {loggedInUser?.name?.charAt(0) || "U"}
            </div>
            {isProfileOpen && (
              <div style={styles.profileDropdown}>
                <div style={styles.dropdownItem}>
                  <strong>Name:</strong> {loggedInUser?.name || "Guest"}
                </div>
                <div style={styles.dropdownItem}>
                  <strong>Email:</strong> {loggedInUser?.email || "N/A"}
                </div>
                <button
                  onClick={handleLogout}
                  style={styles.dropdownLogoutBtn}
                >
                  Logout
                </button>
              </div>
            )}
          </div>
        </div>
      </header>

      <div style={styles.chatWrapper}>
        <div style={styles.chatContainer}>
          <h1 style={styles.chatTitle}>
            🤖 EduBot: An AI-powered Student Assistance 📄
          </h1>

          <div style={styles.controls}>
            <div style={styles.languageSelector}>
              <label htmlFor="lang" style={styles.label}>
                Language:
              </label>
              <select
                id="lang"
                value={lang}
                onChange={(e) => setLang(e.target.value)}
                style={styles.select}
              >
                {languages.map((l) => (
                  <option key={l.code} value={l.code}>
                    {l.name}
                  </option>
                ))}
              </select>
            </div>
            <div style={styles.toggleContainer}>
              <label htmlFor="voiceToggle" style={styles.label}>
                Voice Output:
              </label>
              <input
                type="checkbox"
                id="voiceToggle"
                checked={voiceOutputEnabled}
                onChange={toggleVoiceOutput}
                style={styles.checkboxVisible}
              />
              <span style={{ marginLeft: "5px" }}>
                {voiceOutputEnabled ? "On" : "Off"}
              </span>
            </div>
          </div>

          <div style={styles.chatHistory}>
            {chatHistory.map((item, index) => (
              <div
                key={index}
                style={
                  item.type === "user" ? styles.userMessage : styles.botMessage
                }
              >
                <strong>{item.type === "user" ? "You" : "EduBot"}:</strong>{" "}
                {item.text}
                {item.audioUrl && voiceOutputEnabled && (
                  <audio
                    controls
                    src={item.audioUrl}
                    style={styles.audio}
                    autoPlay
                  >
                    Your browser does not support the audio element.
                  </audio>
                )}
              </div>
            ))}
            {loading && <p style={styles.loading}>Loading...</p>}
          </div>
        </div>

        <div style={styles.inputContainer}>
          <div style={styles.inputWrapper}>
            <input
              type="text"
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Type your question..."
              style={styles.input}
              disabled={loading}
            />
            <button
              onClick={() => handleTextChat(false)}
              style={styles.sendBtn}
              disabled={loading}
              title="Send"
            >
              ➤
            </button>
            <button
              onClick={handleVoiceInput}
              style={styles.voiceBtn}
              disabled={loading}
              title="Voice Input"
            >
              🎤
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

