import React, { useState, useEffect } from "react";
import axios from "axios";
import FooterBottom from "./FooterBottom";

const edubotLogo = process.env.PUBLIC_URL + "/edubot-logo.png";

const Chat = () => {
  const [question, setQuestion] = useState("");
  const [response, setResponse] = useState("");
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [lang, setLang] = useState("en");
  const [audioUrl, setAudioUrl] = useState(null);
  const [voiceOutputEnabled, setVoiceOutputEnabled] = useState(false); // Default off

  const languages = [
    { code: "en", name: "English" },
    { code: "te", name: "Telugu" },
    { code: "hi", name: "Hindi" },
  ];

  useEffect(() => {
    fetchChatHistory();
  }, []);

  const fetchChatHistory = async () => {
    try {
      const res = await axios.get("http://localhost:8000/chat-history");
      setHistory(res.data.history);
    } catch (error) {
      console.error("Error fetching chat history:", error);
    }
  };

  const handleTextChat = async () => {
    if (!question.trim()) return;

    setLoading(true);
    setResponse(""); // Clear previous response
    setAudioUrl(null); // Clear previous audio

    try {
      const endpoint = voiceOutputEnabled ? "/voice-output" : "/chat";
      console.log("Sending request to:", `http://localhost:8000${endpoint}`, "Method: POST");
      const res = await axios.post(
        `http://localhost:8000${endpoint}`,
        { question, lang },
        voiceOutputEnabled ? { responseType: "blob" } : {}
      );

      if (voiceOutputEnabled) {
        const audioBlob = new Blob([res.data], { type: "audio/mp3" });
        const url = URL.createObjectURL(audioBlob);
        console.log("Audio URL:", url);
        setAudioUrl(url);
        setResponse("Audio response generated. Listen below.");
      } else {
        setResponse(res.data.response);
      }
      fetchChatHistory(); // Update history
    } catch (error) {
      setResponse("Error: Could not get a response.");
      console.error("Chat Error:", error);
    } finally {
      setLoading(false);
      setQuestion("");
    }
  };

  const handleVoiceInput = () => {
    const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();
    recognition.lang = lang;
    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setQuestion(transcript);
    };
    recognition.onerror = (event) => {
      setResponse("Error: Could not recognize speech.");
      console.error(event.error);
    };
    recognition.start();
  };

  return (
    <div style={styles.pageContainer}>
      <header style={styles.header}>
        <img src={edubotLogo} alt="EduBot Logo" style={styles.logo} />
      </header>

      <div style={styles.mainContainer}>
        {/* Chat History Sidebar */}
        <div style={styles.sidebar}>
          <h3>Chat History</h3>
          {history.length === 0 ? (
            <p>No history yet.</p>
          ) : (
            history.map((entry, index) => (
              <div key={index} style={styles.historyEntry}>
                <p>
                  <strong>You ({entry.lang}):</strong> {entry.question}
                </p>
                <p>
                  <strong>EduBot:</strong> {entry.answer}
                </p>
              </div>
            ))
          )}
        </div>

        {/* Main Chat Area */}
        <div style={styles.chatContainer}>
          <h1>Chat with EduBot</h1>

          {/* Controls */}
          <div style={styles.controls}>
            <div style={styles.languageSelector}>
              <label htmlFor="lang">Language: </label>
              <select
                id="lang"
                value={lang}
                onChange={(e) => setLang(e.target.value)}
                style={styles.select}
              >
                {languages.map((l) => (
                  <option key={l.code} value={l.code}>
                    {l.name}
                  </option>
                ))}
              </select>
            </div>
            <div style={styles.toggleContainer}>
              <label htmlFor="voiceToggle">Voice Output: </label>
              <input
                type="checkbox"
                id="voiceToggle"
                checked={voiceOutputEnabled}
                onChange={() => setVoiceOutputEnabled(!voiceOutputEnabled)}
              />
            </div>
          </div>

          {/* Chat Box */}
          <div style={styles.chatBox}>
            {response && (
              <div style={styles.response}>
                <strong>EduBot:</strong> {response}
              </div>
            )}
            {loading && <p>Loading...</p>}
            {audioUrl && voiceOutputEnabled && (
              <audio controls src={audioUrl} style={styles.audio} autoPlay>
                Your browser does not support the audio element.
              </audio>
            )}
          </div>

          {/* Input and Buttons */}
          <div style={styles.inputContainer}>
            <input
              type="text"
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              placeholder="Ask a question..."
              style={styles.input}
              disabled={loading}
            />
            <button onClick={handleTextChat} style={styles.sendBtn} disabled={loading}>
              Send
            </button>
            <button onClick={handleVoiceInput} style={styles.voiceBtn} disabled={loading}>
              🎤 Voice Input
            </button>
          </div>
        </div>
      </div>

      <FooterBottom />
    </div>
  );
};

const styles = {
  pageContainer: {
    minHeight: "100vh",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
    backgroundColor: "#f4f4f4",
  },
  header: {
    textAlign: "center",
    padding: "20px",
    backgroundColor: "#fff",
    borderBottom: "1px solid #ddd",
  },
  logo: {
    width: "150px",
  },
  mainContainer: {
    display: "flex",
    flexDirection: "row",
    flexGrow: 1,
  },
  sidebar: {
    width: "25%",
    padding: "20px",
    backgroundColor: "#e9ecef",
    borderRight: "1px solid #ddd",
    overflowY: "auto",
  },
  chatContainer: {
    width: "75%",
    padding: "40px",
    textAlign: "center",
  },
  controls: {
    display: "flex",
    justifyContent: "space-between",
    marginBottom: "20px",
  },
  languageSelector: {
    display: "flex",
    alignItems: "center",
  },
  toggleContainer: {
    display: "flex",
    alignItems: "center",
  },
  select: {
    padding: "5px",
    marginLeft: "10px",
  },
  chatBox: {
    margin: "20px auto",
    maxWidth: "600px",
    minHeight: "200px",
    border: "1px solid #ccc",
    padding: "10px",
    borderRadius: "5px",
    backgroundColor: "#fff",
    overflowY: "auto",
  },
  historyEntry: {
    textAlign: "left",
    margin: "10px 0",
    borderBottom: "1px solid #eee",
  },
  response: {
    textAlign: "left",
    margin: "10px 0",
  },
  inputContainer: {
    display: "flex",
    justifyContent: "center",
    gap: "10px",
  },
  input: {
    padding: "10px",
    width: "400px",
    borderRadius: "5px",
    border: "1px solid #ccc",
  },
  sendBtn: {
    padding: "10px 20px",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
  voiceBtn: {
    padding: "10px 20px",
    backgroundColor: "#28a745",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
  audio: {
    marginTop: "10px",
  },
};

export default Chat;
