import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Navbar from "./components/Navbar";
import FooterBottom from "./components/FooterBottom";
import Signup from "./components/Signup";
import Login from "./components/Login";
import Dashboard from "./components/Dashboard";
import Chat from "./components/Chat"; // Import the updated Chat component
import "./App.css";
import '@fortawesome/fontawesome-free/css/all.min.css';
// Home Component
function Home() {
  return (
    <>
      <Navbar />
      <div className="hero-section">
        <h1>Empower Learning</h1>
        <p>Join us in transforming education for everyone, everywhere.</p>
      </div>

      <div className="explore-section">
        <h2>Explore Edubot</h2>
        <p>
          Edubot is designed to assist students around the clock providing instant
          answers to admission related queries.With its advanced AI capabilities, it
          ensures a seamless experience to users.
        </p>
        <div className="robot-image-container">
          <img src="/robot.png" alt="AI Robot" className="robot-image" />
        </div>
      </div>

      <FooterBottom />
    </>
  );
}

// Protected Route for Dashboard and Chat
const ProtectedRoute = ({ element }) => {
  const loggedInUser = JSON.parse(localStorage.getItem("loggedInUser"));
  return loggedInUser ? element : <Navigate to="/login" />;
};

function App() {
  return (
    <Router>
      <div>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/signup" element={<Signup />} />
          <Route path="/login" element={<Login />} />
          <Route path="/dashboard" element={<ProtectedRoute element={<Dashboard />} />} />
          <Route path="/chat" element={<ProtectedRoute element={<Chat />} />} /> {/* Chat route */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
