import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Login.css";
import Navbar from "./Navbar";
import FooterBottom from "./FooterBottom";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();

    if (!email || !password) {
      alert("Please enter both email and password.");
      return;
    }

    // Retrieve stored users from localStorage
    const users = JSON.parse(localStorage.getItem("users")) || [];

    // Find the user by email and password
    const authenticatedUser = users.find(
      (user) => user.email === email && user.password === password
    );

    if (authenticatedUser) {
      console.log("Authenticated:", authenticatedUser);
      localStorage.setItem("loggedInUser", JSON.stringify(authenticatedUser)); // Store the logged-in user
      navigate("/dashboard"); // Redirect to dashboard
    } else {
      console.log("Failed Login Attempt");
      alert("Invalid email or password. Please try again."); // Inform the user of failure
    }
  };

  return (
    <>
      <Navbar />
      <div className="login-container">
        <h2>Hello, Welcome Back!</h2>
        <form onSubmit={handleLogin}>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">Login</button>
        </form>
      </div>
      <FooterBottom />
    </>
  );
}

export default Login;
