import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios"; // For backend API calls
import "./Signup.css";
import Navbar from "./Navbar";
import FooterBottom from "./FooterBottom";

const Signup = () => {
  const [fullName, setFullName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState(""); // Optional: Keep password field for initial signup
  const navigate = useNavigate();

  const generateOTP = () => {
    return Math.floor(100000 + Math.random() * 900000).toString(); // 6-digit OTP
  };

  const handleSignup = async (e) => {
    e.preventDefault();

    if (!fullName || !email || !password) {
      alert("Please fill in all fields.");
      return;
    }

    const users = JSON.parse(localStorage.getItem("users")) || [];
    if (users.some((user) => user.email === email)) {
      alert("User already exists! Please login.");
      navigate("/login");
      return;
    }

    const otp = generateOTP();
    const newUser = { name: fullName, email, password, otp }; // Store OTP with user
    users.push(newUser);
    localStorage.setItem("users", JSON.stringify(users));

    // Send email with OTP (Backend API call)
    try {
      await axios.post("http://localhost:8000/send-otp", {
        email,
        name: fullName,
        otp,
      });
      alert(`Signup successful! An OTP has been sent to ${email}. Please use it to login.`);
      navigate("/login");
    } catch (error) {
      console.error("Error sending OTP email:", error);
      alert("Signup successful! Check console for OTP (simulated): " + otp);
      navigate("/login");
    }
  };

  return (
    <>
      <Navbar />
      <div className="signup-container">
        <div className="signup-box">
          <h2>Sign Up</h2>
          <form onSubmit={handleSignup}>
            <input
              type="text"
              placeholder="Full Name"
              value={fullName}
              onChange={(e) => setFullName(e.target.value)}
              required
            />
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <button type="submit">Sign Up</button>
          </form>
          <p>Or sign up with</p>
          <div className="social-buttons">
            <button className="google">Sign in with Google</button>
            <button className="facebook">Sign in with Facebook</button>
          </div>
          <p>
            Already have an account?{" "}
            <span onClick={() => navigate("/login")} className="signin-link">
              Login
            </span>
          </p>
        </div>
      </div>
      <FooterBottom />
    </>
  );
};

export default Signup;
