import React, { useEffect, useState } from "react";
import FooterBottom from "./FooterBottom";

const edubotLogo = process.env.PUBLIC_URL + "/edubot-logo.png";
const footerImage = process.env.PUBLIC_URL + "/dashboard.jpg";



const Dashboard = () => {
  const [isScriptLoaded, setIsScriptLoaded] = useState(false);

  useEffect(() => {
    const script = document.createElement("script");
    script.src =
      "https://www.gstatic.com/dialogflow-console/fast/df-messenger/prod/v1/df-messenger.js";
    script.async = true;
    script.onload = () => {
      console.log("Dialogflow script loaded!");
      setIsScriptLoaded(true);
    };
    script.onerror = (error) => {
      console.error("Error loading Dialogflow script:", error);
    };
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  return (
    <div style={styles.pageContainer}>
      {/* Header with EduBot Logo */}
      <header style={styles.header}>
        <img src={edubotLogo} alt="EduBot Logo" style={styles.logo} />
      </header>

      {/* Content Section (Left & Right Text) */}
      <div style={styles.contentContainer}>
        <div style={styles.leftSection}>
          <h1>Meet Edubot: Your AI Powered Student Assistance Chatbot</h1>
        </div>
        <div style={styles.rightSection}>
          <p>
            Edubot provides students with instant access to vital information
            about admissions, scholarships, and placements. With personalized
            assistance available.
          </p>
          <button style={styles.chatBtn}>Chat Now</button>
        </div>
      </div>

      <FooterBottom/>

      {/* Dialogflow Chatbot */}
      {isScriptLoaded ? (
        <df-messenger
          project-id="mineral-order-449616-s3"
          agent-id="ea7007d6-57d4-4dce-a943-71b1d99293e9"
          language-code="en"
        >
          <df-messenger-chat-bubble chat-title="EduBot"></df-messenger-chat-bubble>
        </df-messenger>
      ) : (
        <p>Loading Chat...</p>
      )}

      {/* Chatbot Styling */}
      <style>{`
        df-messenger {
          z-index: 999;
          position: fixed;
          bottom: 16px;
          right: 16px;
          --df-messenger-font-color: #000;
          --df-messenger-font-family: Google Sans;
          --df-messenger-chat-background: #f4f4f4;
          --df-messenger-message-user-background: #d3e3fd;
          --df-messenger-message-bot-background: #fff;
        }
      `}</style>
    </div>
  );
};

// Styles Object
const styles = {
  pageContainer: {
    textAlign: "center",
    fontFamily: "Arial, sans-serif",
    backgroundColor: "#f8f9fa",
    minHeight: "100vh",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "flex-start",
  },
  header: {
    width: "100%",
    display: "flex",
    alignItems: "center",
    padding: "10px 20px",
    backgroundColor: "#007bff",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
  },
  logo: {
    width: "120px",
    height: "auto",
  },
  contentContainer: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    width: "80%",
    marginTop: "50px",
  },
  leftSection: {
    flex: 1,
    textAlign: "left",
    fontSize: "24px",
    fontWeight: "bold",
    color: "#333",
    paddingRight: "20px",
  },
  rightSection: {
    flex: 1,
    textAlign: "left",
    fontSize: "18px",
    color: "#555",
  },
  chatBtn: {
    backgroundColor: "#007bff",
    color: "white",
    padding: "12px 24px",
    border: "none",
    cursor: "pointer",
    borderRadius: "8px",
    fontSize: "18px",
    marginTop: "15px",
  },
  footer: {
    width: "100%",
    display: "flex",
    justifyContent: "center",
    marginTop: "50px",
  },
  footerImage: {
    width: "50%",
    maxHeight: "150px",
    objectFit: "contain",
    borderRadius: "10px",
  },
};

export default Dashboard;
