import React, { useState, useEffect } from "react";
import axios from "axios";
import FooterBottom from "./FooterBottom";

const edubotLogo = process.env.PUBLIC_URL + "/edubot-logo.png";

const Chat = () => {
  const [question, setQuestion] = useState("");
  const [response, setResponse] = useState("");
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [lang, setLang] = useState("en"); // Default language: English
  const [audioUrl, setAudioUrl] = useState(null);

  // Supported languages
  const languages = [
    { code: "en", name: "English" },
    { code: "te", name: "Telugu" },
    { code: "hi", name: "Hindi" },
  ];

  // Load chat history on mount
  useEffect(() => {
    fetchChatHistory();
  }, []);

  const fetchChatHistory = async () => {
    try {
      const res = await axios.get("http://localhost:8000/chat-history");
      setHistory(res.data.history);
    } catch (error) {
      console.error("Error fetching chat history:", error);
    }
  };

  const handleTextChat = async () => {
    if (!question.trim()) return;

    setLoading(true);
    try {
      const res = await axios.post("http://localhost:8000/chat", {
        question,
        lang,
      });
      setResponse(res.data.response);
      fetchChatHistory(); // Refresh history
    } catch (error) {
      setResponse("Error: Could not get a response from the server.");
      console.error(error);
    } finally {
      setLoading(false);
      setQuestion("");
    }
  };

  const handleVoiceChat = async () => {
    if (!question.trim()) return;

    setLoading(true);
    try {
      const res = await axios.post(
        "http://localhost:8000/voice-output",
        { question, lang },
        { responseType: "blob" } // For audio file
      );
      const audioBlob = new Blob([res.data], { type: "audio/mp3" });
      const url = URL.createObjectURL(audioBlob);
      setAudioUrl(url);
      setResponse("Audio response generated. Listen below.");
      fetchChatHistory(); // Refresh history
    } catch (error) {
      setResponse("Error: Could not generate voice response.");
      console.error(error);
    } finally {
      setLoading(false);
      setQuestion("");
    }
  };

  const handleVoiceInput = () => {
    const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();
    recognition.lang = lang;
    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setQuestion(transcript);
    };
    recognition.onerror = (event) => {
      setResponse("Error: Could not recognize speech.");
      console.error(event.error);
    };
    recognition.start();
  };

  return (
    <div style={styles.pageContainer}>
      <header style={styles.header}>
        <img src={edubotLogo} alt="EduBot Logo" style={styles.logo} />
      </header>

      <div style={styles.chatContainer}>
        <h1>Chat with EduBot</h1>

        {/* Language Selector */}
        <div style={styles.languageSelector}>
          <label htmlFor="lang">Language: </label>
          <select
            id="lang"
            value={lang}
            onChange={(e) => setLang(e.target.value)}
            style={styles.select}
          >
            {languages.map((l) => (
              <option key={l.code} value={l.code}>
                {l.name}
              </option>
            ))}
          </select>
        </div>

        {/* Chat History */}
        <div style={styles.chatBox}>
          {history.map((entry, index) => (
            <div key={index} style={styles.historyEntry}>
              <p>
                <strong>You ({entry.lang}):</strong> {entry.question}
              </p>
              <p>
                <strong>EduBot:</strong> {entry.answer}
              </p>
            </div>
          ))}
          {response && (
            <div style={styles.response}>
              <strong>EduBot:</strong> {response}
            </div>
          )}
          {loading && <p>Loading...</p>}
          {audioUrl && <audio controls src={audioUrl} style={styles.audio} />}
        </div>

        {/* Input and Buttons */}
        <div style={styles.inputContainer}>
          <input
            type="text"
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            placeholder="Ask a question..."
            style={styles.input}
            disabled={loading}
          />
          <button onClick={handleTextChat} style={styles.sendBtn} disabled={loading}>
            Send
          </button>
          <button onClick={handleVoiceInput} style={styles.voiceBtn} disabled={loading}>
            ðŸŽ¤ Voice Input
          </button>
          <button onClick={handleVoiceChat} style={styles.voiceBtn} disabled={loading}>
            ðŸ”Š Voice Output
          </button>
        </div>
      </div>

      <FooterBottom />
    </div>
  );
};

// Styles
const styles = {
  pageContainer: {
    minHeight: "100vh",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
    backgroundColor: "#f4f4f4",
  },
  header: {
    textAlign: "center",
    padding: "20px",
    backgroundColor: "#fff",
    borderBottom: "1px solid #ddd",
  },
  logo: {
    width: "150px",
  },
  chatContainer: {
    padding: "40px",
    textAlign: "center",
    flexGrow: 1,
  },
  languageSelector: {
    marginBottom: "20px",
  },
  select: {
    padding: "5px",
    marginLeft: "10px",
  },
  chatBox: {
    margin: "20px auto",
    maxWidth: "600px",
    minHeight: "200px",
    border: "1px solid #ccc",
    padding: "10px",
    borderRadius: "5px",
    backgroundColor: "#fff",
    overflowY: "auto",
  },
  historyEntry: {
    textAlign: "left",
    margin: "10px 0",
    borderBottom: "1px solid #eee",
  },
  response: {
    textAlign: "left",
    margin: "10px 0",
  },
  inputContainer: {
    display: "flex",
    justifyContent: "center",
    gap: "10px",
  },
  input: {
    padding: "10px",
    width: "400px",
    borderRadius: "5px",
    border: "1px solid #ccc",
  },
  sendBtn: {
    padding: "10px 20px",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
  voiceBtn: {
    padding: "10px 20px",
    backgroundColor: "#28a745",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
  audio: {
    marginTop: "10px",
  },
};

export default Chat;
