import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Chat = () => {
  const [question, setQuestion] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [lang, setLang] = useState("en");
  const [voiceOutputEnabled, setVoiceOutputEnabled] = useState(false);
  const [isProfileOpen, setIsProfileOpen] = useState(false);

  const navigate = useNavigate();
  const loggedInUser = JSON.parse(localStorage.getItem("loggedInUser")) || {};

  const languages = [
    { code: "en", name: "English" },
    { code: "te", name: "Telugu" },
    { code: "hi", name: "Hindi" },
  ];

  // Same handleTextChat, handleVoiceInput, toggleVoiceOutput, startNewChat functions as above

  const handleLogout = () => {
    localStorage.removeItem("loggedInUser");
    navigate("/");
  };

  const toggleProfileDropdown = () => {
    setIsProfileOpen((prev) => !prev);
  };

  return (
    <div style={styles.pageContainer}>
      <div style={styles.sidebar}>
        <div style={styles.sidebarContent}></div> {/* Placeholder for sidebar content */}
        <div style={styles.profileContainer}>
          <div style={styles.profileTrigger} onClick={toggleProfileDropdown}>
            {loggedInUser?.name?.charAt(0)?.toUpperCase() ||
              loggedInUser?.email?.charAt(0)?.toUpperCase() ||
              "U"}
          </div>
          {isProfileOpen && (
            <div style={styles.profileDropdown}>
              <div style={styles.profileItem}>
                <strong>Name:</strong> {loggedInUser?.name || "User"}
              </div>
              <div style={styles.profileItem}>
                <strong>Email:</strong> {loggedInUser?.email || "N/A"}
              </div>
              <button onClick={handleLogout} style={styles.logoutBtn}>
                Logout
              </button>
            </div>
          )}
        </div>
      </div>

      <div style={styles.mainContent}>
        <div style={styles.chatWrapper}>
          <div style={styles.chatContainer}>
            <h1 style={styles.chatTitle}>
              🤖 EduBot: An AI-powered Student Assistance 📄
            </h1>
            <div style={styles.controls}>
              <div style={styles.languageSelector}>
                <label htmlFor="lang" style={styles.label}>
                  Language:
                </label>
                <select
                  id="lang"
                  value={lang}
                  onChange={(e) => setLang(e.target.value)}
                  style={styles.select}
                >
                  {languages.map((l) => (
                    <option key={l.code} value={l.code}>
                      {l.name}
                    </option>
                  ))}
                </select>
              </div>
              <div style={styles.toggleContainer}>
                <label htmlFor="voiceToggle" style={styles.label}>
                  Voice Output:
                </label>
                <input
                  type="checkbox"
                  id="voiceToggle"
                  checked={voiceOutputEnabled}
                  onChange={toggleVoiceOutput}
                  style={styles.checkboxVisible}
                />
                <span style={{ marginLeft: "5px" }}>
                  {voiceOutputEnabled ? "On" : "Off"}
                </span>
              </div>
            </div>
            <div style={styles.chatHistory}>
              {chatHistory.map((item, index) => (
                <div
                  key={index}
                  style={
                    item.type === "user" ? styles.userMessage : styles.botMessage
                  }
                >
                  <strong>{item.type === "user" ? "You" : "EduBot"}:</strong>{" "}
                  {item.text}
                  {item.audioUrl && voiceOutputEnabled && (
                    <audio
                      controls
                      src={item.audioUrl}
                      style={styles.audio}
                      autoPlay
                    >
                      Your browser does not support the audio element.
                    </audio>
                  )}
                </div>
              ))}
              {loading && <p style={styles.loading}>Loading...</p>}
            </div>
          </div>
          <div style={styles.inputContainer}>
            <input
              type="text"
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              placeholder="Ask a question..."
              style={styles.input}
              disabled={loading}
            />
            <button
              onClick={() => handleTextChat(false)}
              style={styles.sendBtn}
              disabled={loading}
            >
              Send
            </button>
            <button
              onClick={startNewChat}
              style={styles.newChatBtn}
              disabled={loading || !question.trim()}
            >
              New Chat
            </button>
            <button
              onClick={handleVoiceInput}
              style={styles.voiceBtn}
              disabled={loading}
            >
              🎤
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

// Updated Styles
const styles = {
  pageContainer: {
    minHeight: "100vh",
    display: "flex",
    backgroundColor: "#f0f2f5",
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    overflow: "hidden",
  },
  sidebar: {
    width: "250px",
    backgroundColor: "#ffffff",
    boxShadow: "2px 0 4px rgba(0, 0, 0, 0.1)",
    display: "flex",
    flexDirection: "column",
    flexShrink: 0,
  },
  sidebarContent: {
    flexGrow: 1,
  },
  profileContainer: {
    padding: "20px",
    position: "relative",
  },
  profileTrigger: {
    width: "40px",
    height: "40px",
    backgroundColor: "#1a73e8",
    color: "#fff",
    borderRadius: "50%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "1.5rem",
    fontWeight: "bold",
    cursor: "pointer",
  },
  profileDropdown: {
    position: "absolute",
    bottom: "60px",
    left: "20px",
    backgroundColor: "#ffffff",
    borderRadius: "5px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    padding: "10px",
    width: "200px",
    display: "flex",
    flexDirection: "column",
    zIndex: 10,
  },
  profileItem: {
    padding: "10px",
    fontSize: "1rem",
    color: "#333",
    borderBottom: "1px solid #ddd",
  },
  logoutBtn: {
    padding: "10px",
    backgroundColor: "#dc3545",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "1rem",
    fontWeight: "500",
    textAlign: "center",
    marginTop: "10px",
    transition: "background-color 0.3s",
  },
  mainContent: {
    flexGrow: 1,
    display: "flex",
    flexDirection: "column",
  },
  chatWrapper: {
    flexGrow: 1,
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    padding: "20px",
    width: "100%",
    maxWidth: "800px",
    margin: "0 auto",
    overflow: "hidden",
  },
  chatContainer: {
    width: "100%",
    backgroundColor: "#ffffff",
    borderRadius: "10px",
    padding: "20px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    display: "flex",
    flexDirection: "column",
    flexShrink: 0,
    height: "500px",
    overflow: "hidden",
  },
  chatTitle: {
    fontSize: "2rem",
    color: "#1a73e8",
    marginBottom: "20px",
    fontWeight: "700",
    textAlign: "center",
    flexShrink: 0,
  },
  controls: {
    display: "flex",
    justifyContent: "space-between",
    width: "100%",
    marginBottom: "20px",
    flexShrink: 0,
  },
  languageSelector: {
    display: "flex",
    alignItems: "center",
  },
  toggleContainer: {
    display: "flex",
    alignItems: "center",
  },
  label: {
    fontSize: "1rem",
    color: "#555",
    marginRight: "10px",
