import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import FooterBottom from "./FooterBottom";

const edubotLogo = process.env.PUBLIC_URL + "/edubot-logo.png";

// Define styles outside the component
const styles = {
  pageContainer: {
    minHeight: "100vh",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
    backgroundColor: "#f4f4f4",
  },
  header: {
    textAlign: "center",
    padding: "20px",
    backgroundColor: "#fff",
    borderBottom: "1px solid #ddd",
  },
  logo: {
    width: "150px",
  },
  contentContainer: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-around",
    alignItems: "center",
    padding: "40px",
    flexGrow: 1,
  },
  leftSection: {
    maxWidth: "50%",
  },
  rightSection: {
    maxWidth: "40%",
    textAlign: "left",
  },
  chatBtn: {
    marginTop: "20px",
    padding: "10px 20px",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "16px",
  },
  dashboardImage: {
    marginTop: "20px",
    width: "100%",
    maxWidth: "300px", // Adjust as needed
    height: "auto",
    borderRadius: "8px",
  },
};

const Dashboard = () => {
  const [isScriptLoaded, setIsScriptLoaded] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const script = document.createElement("script");
    script.src =
      "https://www.gstatic.com/dialogflow-console/fast/df-messenger/prod/v1/df-messenger.js";
    script.async = true;
    script.onload = () => {
      console.log("Dialogflow script loaded!");
      setIsScriptLoaded(true);
    };
    script.onerror = (error) => {
      console.error("Error loading Dialogflow script:", error);
    };
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  const handleChatNow = () => {
    navigate("/chat");
  };

  return (
    <div style={styles.pageContainer}>
      <header style={styles.header}>
        <img src={edubotLogo} alt="EduBot Logo" style={styles.logo} />
      </header>

      <div style={styles.contentContainer}>
        <div style={styles.leftSection}>
          <h1>Meet Edubot: Your AI Powered Student Assistance Chatbot</h1>
        </div>
        <div style={styles.rightSection}>
          <p>
            Edubot provides students with instant access to vital information
            about admissions, scholarships, and placements. With personalized
            assistance available.
          </p>
          <button style={styles.chatBtn} onClick={handleChatNow}>
            Chat Now
          </button>
          <img
            src="/dimg.png" // Direct path to public/ folder
            alt="Dashboard Illustration"
            style={styles.dashboardImage}
          />
        </div>
      </div>

      <FooterBottom />
    </div>
  );
};

export default Dashboard;
