import React, { useState, useEffect } from "react";
import axios from "axios";
import FooterBottom from "./FooterBottom";

const edubotLogo = process.env.PUBLIC_URL + "/edubot-logo.png";

const Chat = () => {
  const [question, setQuestion] = useState("");
  const [response, setResponse] = useState("");
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [lang, setLang] = useState("en");
  const [audioUrl, setAudioUrl] = useState(null);
  const [voiceOutputEnabled, setVoiceOutputEnabled] = useState(false);
  const [isFirstMessage, setIsFirstMessage] = useState(true);

  const languages = [
    { code: "en", name: "English" },
    { code: "te", name: "Telugu" },
    { code: "hi", name: "Hindi" },
  ];

  useEffect(() => {
    fetchChatHistory();
  }, []);

  const fetchChatHistory = async () => {
    try {
      const res = await axios.get("http://localhost:8000/chat-history");
      console.log("Fetched history:", res.data.history); // Debug log
      setHistory(Array.isArray(res.data.history) ? res.data.history : []);
    } catch (error) {
      console.error("Error fetching chat history:", error);
      setHistory([]); // Fallback to empty array on error
    }
  };

  const handleTextChat = async () => {
    if (!question.trim()) return;

    setLoading(true);
    setResponse("");
    setAudioUrl(null);

    try {
      const textRes = await axios.post("http://localhost:8000/chat", {
        question,
        lang,
        new_session: isFirstMessage,
      });
      setResponse(textRes.data.response);

      if (voiceOutputEnabled) {
        const voiceRes = await axios.post(
          "http://localhost:8000/voice-output",
          { question, lang, new_session: isFirstMessage },
          { responseType: "blob" }
        );
        const audioBlob = new Blob([voiceRes.data], { type: "audio/mp3" });
        const url = URL.createObjectURL(audioBlob);
        console.log("Audio URL:", url);
        setAudioUrl(url);
      }

      fetchChatHistory();
      setIsFirstMessage(false);
    } catch (error) {
      setResponse("Error: Could not get a response.");
      console.error("Chat Error:", error);
    } finally {
      setLoading(false);
      setQuestion("");
    }
  };

  const handleVoiceInput = () => {
    const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();
    recognition.lang = lang;
    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setQuestion(transcript);
    };
    recognition.onerror = (event) => {
      setResponse("Error: Could not recognize speech.");
      console.error(event.error);
    };
    recognition.start();
  };

  const toggleVoiceOutput = () => {
    setVoiceOutputEnabled((prev) => !prev);
    console.log("Voice Output Enabled:", !voiceOutputEnabled);
  };

  return (
    <div style={styles.pageContainer}>
      <header style={styles.header}>
        <img src={edubotLogo} alt="EduBot Logo" style={styles.logo} />
      </header>

      <div style={styles.mainContainer}>
        {/* Chat History Sidebar */}
        <div style={styles.sidebar}>
          <h3 style={styles.sidebarTitle}>Chat Sessions</h3>
          {history.length === 0 ? (
            <p style={styles.sidebarEmpty}>No sessions yet.</p>
          ) : (
            history.map((session) => (
              <div key={session.session_id} style={styles.sessionBlock}>
                <h4 style={styles.sessionTitle}>
                  {session.session_id}{" "}
                  {session.entries && session.entries.length > 0
                    ? `(${new Date(session.entries[0].timestamp).toLocaleString()})`
                    : "(No entries)"}
                </h4>
                {session.entries && session.entries.length > 0 ? (
                  session.entries.map((entry, entryIndex) => (
                    <div key={entryIndex} style={styles.historyEntry}>
                      <p style={styles.historyQuestion}>
                        <strong>You ({entry.lang}):</strong> {entry.question}
                      </p>
                      <p style={styles.historyAnswer}>
                        <strong>EduBot:</strong> {entry.answer}
                      </p>
                    </div>
                  ))
                ) : (
                  <p style={styles.sidebarEmpty}>No messages in this session.</p>
                )}
              </div>
            ))
          )}
        </div>

        {/* Main Chat Area */}
        <div style={styles.chatContainer}>
          <h1 style={styles.chatTitle}>Chat with EduBot</h1>

          {/* Controls */}
          <div style={styles.controls}>
            <div style={styles.languageSelector}>
              <label htmlFor="lang" style={styles.label}>
                Language:
              </label>
              <select
                id="lang"
                value={lang}
                onChange={(e) => setLang(e.target.value)}
                style={styles.select}
              >
                {languages.map((l) => (
                  <option key={l.code} value={l.code}>
                    {l.name}
                  </option>
                ))}
              </select>
            </div>
            <div style={styles.toggleContainer}>
              <label htmlFor="voiceToggle" style={styles.label}>
                Voice Output:
              </label>
              <div style={styles.switch}>
                <input
                  type="checkbox"
                  id="voiceToggle"
                  checked={voiceOutputEnabled}
                  onChange={toggleVoiceOutput}
                  style={styles.checkbox}
                />
                <span style={voiceOutputEnabled ? styles.sliderOn : styles.sliderOff}></span>
              </div>
            </div>
          </div>

          {/* Chat Box */}
          <div style={styles.chatBox}>
            {response && (
              <div style={styles.response}>
                <strong>EduBot:</strong> {response}
              </div>
            )}
            {loading && <p style={styles.loading}>Loading...</p>}
            {audioUrl && voiceOutputEnabled && (
              <audio controls src={audioUrl} style={styles.audio} autoPlay>
                Your browser does not support the audio element.
              </audio>
            )}
          </div>

          {/* Input and Buttons */}
          <div style={styles.inputContainer}>
            <input
              type="text"
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              placeholder="Ask a question..."
              style={styles.input}
              disabled={loading}
            />
            <button onClick={handleTextChat} style={styles.sendBtn} disabled={loading}>
              Send
            </button>
            <button onClick={handleVoiceInput} style={styles.voiceBtn} disabled={loading}>
              🎤
            </button>
          </div>
        </div>
      </div>

      <FooterBottom />
    </div>
